import React, { Component } from 'react';
import DayPickerInput from "react-day-picker/DayPickerInput";
import "react-day-picker/lib/style.css";
import { formatDate, parseDate } from "react-day-picker/moment";
import "moment/locale/it";

import { Header, Input, Form, Button } from 'semantic-ui-react'
import * as ROUTES from '../../constants/routes';

const INITIAL_STATE = {
    startDate: new Date,
    endDate: new Date,
    password: password,
    error: null,
  };

  export default class GenerateQRCode extends Component {
    constructor(props) {
      super(props);
      this.state = { ...INITIAL_STATE };
      this.handleChange = this.handleChange.bind(this);
    }

    handleChange(date) {
        this.setState({
          startDate: date,
          endDate: date,
        });
      }

    onSubmit = event => {
      const { password } = this.state;

      this.props.firebase
        if (password === this.state.password)
        {
          this.setState({ ...INITIAL_STATE });
          this.props.history.push(ROUTES.STRIPE);
        }
      event.preventDefault();
    };

    onChange = event => {
      this.setState({ [event.target.name]: event.target.value });
    };

    render() {
      const { startDate, endDate, error } = this.state;
      const isInvalid = startDate === '' && endDate === '';

      return (

    <div>
    <Header as='h1' textAlign='center'>Generate QR Code</Header>
      <Form onSubmit={this.onSubmit}>
        <Form.Field>
        <label>Select Event Dates</label>
        <DayPickerInput
              formatDate={formatDate}
              parseDate={parseDate}
              placeholder={`${formatDate(new Date())}`}
            />
        </Form.Field>
        <Form.Field>
        <DayPickerInput
                formatDate={formatDate}
                parseDate={parseDate}
                placeholder={`${formatDate(new Date())}`}
              />
        <Form.Field>
        </Form.Field>
        <label>Retype Account Password</label>
        <Input
          name="password"
          value={password}
          onChange={this.onChange}
          type="password"
          placeholder="Password"
        />
        </Form.Field>
        <Button fluid disabled={isInvalid} type="submit">
          Move to Payment
        </Button>

        {error && <p>{error.message}</p>}
      </Form>
      </div>
);
}}
