import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Header, Input, Form, Button } from 'semantic-ui-react'
import * as ROUTES from '../../constants/routes';

const INITIAL_STATE = {
    startDate: new Date,
    endDate: new Date,
    password: '',
    error: null,
  };

  const options = [
    { key: 1, text: '1', value: 1 },
    { key: 2, text: '2', value: 2 },
    { key: 3, text: '3', value: 3 },
    { key: 4, text: '4', value: 4 },
    { key: 5, text: '5', value: 5 },
    { key: 6, text: '6', value: 6 },
    { key: 7, text: '7', value: 7 },
    { key: 8, text: '8', value: 8 },
    { key: 9, text: '9', value: 9 },

  ]


  export default class GenerateQRCode extends Component {
    constructor(props) {
      super(props);
      this.state = { ...INITIAL_STATE };
      this.handleChange = this.handleChange.bind(this);
    }

    handleChange(startDate, endDate) {
        this.setState({
          startDate: startDate,
          endDate: endDate,
        });
      }

    onSubmit = event => {
      const { email, password } = this.state;

      this.props.firebase
        .doSignInWithEmailAndPassword(email, password)
        .then(() => {
          this.setState({ ...INITIAL_STATE });
          this.props.history.push(ROUTES.HOME);
        })
        .catch(error => {
          this.setState({ error });
        });

      event.preventDefault();
    };

    onChange = event => {
      this.setState({ [event.target.name]: event.target.value });
    };

    render() {
      const { email, password, error } = this.state;
      const isInvalid = password === '' || email === '';

      return (

    <div>
    <Header as='h1' textAlign='center'>Generate QR Code</Header>
      <Form onSubmit={this.onSubmit}>
       <Form.Field>
      <label>Start Date</label>
        <DatePicker
          selected={this.state.startDate}
          onChange={this.handleChange}
        />
        </Form.Field>
        <Form.Field>
        <label>How many days is your event</label>
        <Menu compact>
            <Dropdown text='Dropdown' options={options} simple item />
        </Menu>
        </Form.Field>
        <Form.Field>
        <label>Retype Password</label>
        <Input
          value={password}
          onChange={this.onChange}
          type="password"
          placeholder="Password"
        />
        </Form.Field>

        <Button fluid disabled={isInvalid} type="submit">
          Generate QR Code
        </Button>

        {error && <p>{error.message}</p>}
      </Form>
      </div>
);
      }}

