import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Header, Input, Form, Button } from 'semantic-ui-react'
import * as ROUTES from '../../constants/routes';

const INITIAL_STATE = {
    startDate: new Date,
    endDate: new Date,
    password: '',
    error: null,
  };

  export default class GenerateQRCode extends Component {
    constructor(props) {
      super(props);
      this.state = { ...INITIAL_STATE };
      this.handleChange = this.handleChange.bind(this);
    }

    handleChange(startdate, enddate) {
        this.setState({
          startDate: startdate,
          endDate: enddate,
        });
      }

    onSubmit = event => {

    };

    onChange = event => {
      this.setState({ [event.target.name]: event.target.value });
    };

    render() {
      const { email, password, error } = this.state;
      const isInvalid = password === '' || email === '';

      return (

    <div>
    <Header as='h1' textAlign='center'>Generate QR Code</Header>
      <Form onSubmit={this.onSubmit}>
       <Form.Field>
      <label>Start Date</label>
        <DatePicker
          selected={this.state.startDate}
          onChange={this.handleChange}
        />
        </Form.Field>
        <Form.Field>
        <label>End Date</label>
        <DatePicker
          selected={this.state.endDate}
          onChange={this.handleChange}
        />
        </Form.Field>
        <Form.Field>
        <label>Retype Password</label>
        <Input
          name="password"
          value={password}
          onChange={this.onChange}
          type="password"
          placeholder="Password"
        />
        </Form.Field>
        <Button fluid disabled={isInvalid} type="submit">
          Move to Payment
        </Button>

        {error && <p>{error.message}</p>}
      </Form>
      </div>
);
      }}

