import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Header, Input, Form, Button } from 'semantic-ui-react'
import * as ROUTES from '../../constants/routes';

const INITIAL_STATE = {
    startDate: null,
    endDate: null,
    password: '',
    error: null,
  };

  export default class GenerateQRCode extends Component {
    constructor(props) {
      super(props);
      this.state = { ...INITIAL_STATE, startDate: new Date(), endDate: new Date() };
      this.handleChange = this.handleChange.bind(this);
    }

    handleChange(date) {
        this.setState({
          startDate: date,
          endDate: date
        });
      }

    onSubmit = event => {
      const { email, password } = this.state;

      this.props.firebase
        .doSignInWithEmailAndPassword(email, password)
        .then(() => {
          this.setState({ ...INITIAL_STATE });
          this.props.history.push(ROUTES.HOME);
        })
        .catch(error => {
          this.setState({ error });
        });

      event.preventDefault();
    };

    onChange = event => {
      this.setState({ [event.target.name]: event.target.value });
    };

    render() {
      const { email, startDate, endDate, password, error } = this.state;
      const isInvalid = password === '' || email === '';

      return (

    <div>
    <Header as='h1' textAlign='center'>Generate QR Code</Header>
      <Form onSubmit={this.onSubmit}>
       <Form.Field>
      <label>Start Date</label>
        <DatePicker
          selected={this.state.startDate}
          onChange={this.handleChange}
        />
        </Form.Field>
        <Form.Field>
        <label>End Date</label>
        <DatePicker
          selected={this.state.startDate}
          onChange={this.handleChange}
        />
        </Form.Field>
        <Form.Field>
        <label>Retype Password</label>
        <Input
          value={password}
          onChange={this.onChange}
          type="password"
          placeholder="Password"
        />
        </Form.Field>

        <Button fluid disabled={isInvalid} type="submit">
          Generate QR Code
        </Button>

        {error && <p>{error.message}</p>}
      </Form>
      </div>
);
      }}

