import React from 'react';
import { compose } from 'recompose';
import { Header, Segment, Grid } from 'semantic-ui-react'

import { withAuthorization, withEmailVerification } from '../Session';
import Messages from '../Messages';

const HomePage = () => (
  <div>
    <Header>Home Page</Header>
    <Header as='h3'>Are you holding an event and want to be able to communitcate to all you attendees?</Header>
    <Header as='h4'>For $1 you are able to generate a unique QR code for your event. Your attendees will be able to downlaod our app scan your QR code and you can broadcast messages directly to there phones without using wifi or data.</Header>
    <Header as='h3'>It is a lightweight, seemless, solution to sending out texts or trying to reach your audience</Header>
  </div>
);

const condition = authUser => !!authUser;

export default compose(
  withEmailVerification,
  withAuthorization(condition),
)(HomePage);
